// Spiral Order Matrix I
// https://www.interviewbit.com/problems/spiral-order-matrix-i/

class Pair
{
public:
	int idx, val;
	
	Pair(int i, int v):idx(i), val(v) 
	{}

	bool operator < (const Pair rhs ) const
	{
		return val < rhs.val;
	}
};

int maximumGap(const vector<int> &A) {
	
	int n = A.size(); 
	if (n == 0) return -1;
    if (n == 1) return 0;
     int result = -1;      
	vector<Pair> idxValPairs;

	for(int i=0; i<n; i++)
		idxValPairs.push_back(Pair(i, A[i]));

	sort(idxValPairs.begin(), idxValPairs.end());
	int maxIdx = idxValPairs[n-1].idx;
	for(int i=n-2; i>=0 ; i--)
	{
		result = max(result, maxIdx - idxValPairs[i].idx);
		maxIdx = max(maxIdx, idxValPairs[i].idx);
	}

	return result;
}

int testCase0()
{
	int x[] = {3, 5, 2, 4};
	vector<int> a(x, x + sizeof x / sizeof x[0]);
	return maximumGap(a);
}

